        const response = await fetch('/api/v1/users/me', {
          headers: {
            'Authorization': `Bearer ${session.access_token}`,
          },
        });
        const userData = await response.json();

        if (response.ok && userData.data) {
          // Check if key onboarding fields are null
          const userProfile = userData.data;
          if (!userProfile.goals || !userProfile.units) { // Example: checking for goals and units
            router.push('/onboarding');
          } else {
            setLoading(false);
          }
        } else {
          // Error fetching profile, treat as un-onboarded or error
          console.error("Error fetching user profile:", userData);
          router.push('/onboarding');
        }
      } catch (error) {
        console.error("Network error fetching user profile:", error);
        router.push('/onboarding'); // Redirect to onboarding on network error
      }
    };

    checkAuthAndOnboarding();
  }, [router]);

  if (loading) {
    return (
      <div className="flex min-h-screen items-center justify-center">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen items-center justify-center bg-zinc-50 font-sans dark:bg-black">
      <main className="flex min-h-screen w-full max-w-3xl flex-col items-center justify-between py-32 px-16 bg-white dark:bg-black sm:items-start">
        <Image
          className="dark:invert"
          src="/next.svg"
          alt="Next.js logo"
          width={100}
          height={20}
          priority
        />
        <div className="flex flex-col items-center gap-6 text-center sm:items-start sm:text-left">
          <h1 className="max-w-xs text-3xl font-semibold leading-10 tracking-tight text-black dark:text-zinc-50">
            Welcome to the AI Personal Training Advisor!
          </h1>
          <p className="max-w-md text-lg leading-8 text-zinc-600 dark:text-zinc-400">
            Your personalized fitness journey starts here.
          </p>
        </div>
        <div className="flex flex-col gap-4 text-base font-medium sm:flex-row">
          <a
            className="flex h-12 w-full items-center justify-center gap-2 rounded-full bg-foreground px-5 text-background transition-colors hover:bg-[#383838] dark:hover:bg-[#ccc] md:w-[158px]"
            href="/login" // Link to your login page
          >
            Go to Login
          </a>
          {/* Temporary link to Profile Page for development */}
          <a
            className="flex h-12 w-full items-center justify-center gap-2 rounded-full bg-blue-600 px-5 text-white transition-colors hover:bg-blue-700 md:w-[158px]"
            href="/profile"
          >
            Go to Profile
          </a>
        </div>
      </main>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation'; // Import useRouter

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true); // Toggle between Login and Sign Up
  const [error, setError] = useState<string | null>(null); // For displaying errors
  const router = useRouter(); // Initialize useRouter

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setError(null); // Clear previous errors

    const endpoint = isLogin ? '/api/v1/auth/login' : '/api/v1/auth/signup';
    const body = { email, password };

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(body),
      });

      const data = await response.json();

      if (response.ok) {
        // Assuming the backend returns a token or session info on successful login/signup
        console.log('Success:', data);
        // For now, we'll just log and redirect. In a real app, store securely (e.g., http-only cookies)
        // localStorage.setItem('authToken', data.access_token); // Example for client-side token (not recommended for production)
        alert('Authentication successful! Redirecting...'); // Use alert for immediate feedback
        router.push('/'); // Redirect to the authenticated page (e.g., home)
      } else {
        setError(data.detail || 'Authentication failed');
        console.error('Error:', data);
      }
    } catch (err) {
      setError('Network error or server unavailable');
      console.error('Fetch error:', err);
    }
  };

  const handleGoogleLogin = () => {
    // Redirect to the backend endpoint that initiates Google OAuth
    window.location.href = '/api/v1/auth/google';
  };

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: '50px auto', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={handleSubmit}>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="email" style={{ display: 'block', marginBottom: '5px' }}>Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="password" style={{ display: 'block', marginBottom: '5px' }}>Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
          />
        </div>
        <button type="submit" style={{ padding: '10px 15px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
          {isLogin ? 'Log In' : 'Sign Up'}
        </button>
      </form>
      <div style={{ marginTop: '20px', textAlign: 'center' }}>
        <button
          onClick={handleGoogleLogin}
          style={{
            padding: '10px 15px',
            backgroundColor: '#db4437', // Google's brand color
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '100%'
          }}
        >
          <img src="/google-icon.svg" alt="Google icon" style={{ marginRight: '10px', height: '18px' }} />
          Continue with Google
        </button>
      </div>
      <p style={{ marginTop: '20px', textAlign: 'center' }}>
        {isLogin ? "Don't have an account?" : "Already have an account?"}{' '}
        <button
          onClick={() => setIsLogin(!isLogin)}
          style={{ background: 'none', border: 'none', color: '#007bff', cursor: 'pointer', textDecoration: 'underline' }}
        >
          {isLogin ? 'Sign Up' : 'Log In'}
        </button>
      </p>
    </div>
  );
}

