import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import '@testing-library/jest-dom';
import ProfilePage from '../page'; // Adjust path as necessary
import * as userService from '@/app/lib/api/user'; // Mock the API service

// Mock the API service functions
jest.mock('@/app/lib/api/user', () => ({
  getUserProfile: jest.fn(),
  updateUserProfile: jest.fn(),
}));

const mockUserProfile = {
  id: 'user123',
  email: 'test@example.com',
  name: 'Test User',
  goals: { fitness: 'strength', weight: 'gain' },
  preferences: { notifications: true },
  equipment: ['dumbbells', 'barbell'],
  injuries: 'none',
  units: 'metric',
};

describe('ProfilePage', () => {
  beforeEach(() => {
    // Reset mocks before each test
    (userService.getUserProfile as jest.Mock).mockClear();
    (userService.updateUserProfile as jest.Mock).mockClear();
  });

  test('renders loading state initially', () => {
    (userService.getUserProfile as jest.Mock).mockReturnValueOnce(new Promise(() => {})); // Never resolves
    render(<ProfilePage />);
    expect(screen.getByText('Loading profile...')).toBeInTheDocument();
  });

  test('renders user profile after successful API call', async () => {
    (userService.getUserProfile as jest.Mock).mockResolvedValueOnce(mockUserProfile);
    render(<ProfilePage />);

    await waitFor(() => {
      expect(screen.getByText('Test User')).toBeInTheDocument();
      expect(screen.getByText('test@example.com')).toBeInTheDocument();
      const goalsPreElement = screen.getByText('Goals:').nextElementSibling;
      expect(JSON.parse(goalsPreElement?.textContent || '{}')).toEqual(mockUserProfile.goals);

      const preferencesPreElement = screen.getByText('Preferences:').nextElementSibling;
      expect(JSON.parse(preferencesPreElement?.textContent || '{}')).toEqual(mockUserProfile.preferences);
      expect(screen.getByText('dumbbells, barbell')).toBeInTheDocument();
      expect(screen.getByText('none')).toBeInTheDocument();
      expect(screen.getByText('metric')).toBeInTheDocument();
    });

    expect(screen.getByRole('button', { name: /edit profile/i })).toBeInTheDocument();
  });

  test('renders error message if API call fails', async () => {
    const errorMessage = 'Failed to fetch profile';
    (userService.getUserProfile as jest.Mock).mockRejectedValueOnce(new Error(errorMessage));
    render(<ProfilePage />);

    await waitFor(() => {
      expect(screen.getByText(`Error: ${errorMessage}`)).toBeInTheDocument();
    });
  });

  test('switches to editing mode when Edit Profile button is clicked', async () => {
    (userService.getUserProfile as jest.Mock).mockResolvedValueOnce(mockUserProfile);
    render(<ProfilePage />);

    await waitFor(() => {
      fireEvent.click(screen.getByRole('button', { name: /edit profile/i }));
    });

    expect(screen.getByDisplayValue('Test User')).toBeInTheDocument();
    expect(screen.getByRole('button', { name: /save changes/i })).toBeInTheDocument();
    expect(screen.getByRole('button', { name: /cancel/i })).toBeInTheDocument();
  });

  test('updates user profile on form submission', async () => {
    (userService.getUserProfile as jest.Mock).mockResolvedValueOnce(mockUserProfile);
    const updatedName = 'Updated User';
    const updatedUnits = 'imperial';
    const updatedGoals = { fitness: 'endurance' };

    (userService.updateUserProfile as jest.Mock).mockResolvedValueOnce({
      ...mockUserProfile,
      name: updatedName,
      units: updatedUnits,
      goals: updatedGoals,
    });

    render(<ProfilePage />);
    await waitFor(() => fireEvent.click(screen.getByRole('button', { name: /edit profile/i })));

    fireEvent.change(screen.getByLabelText(/name/i), { target: { value: updatedName } });
    fireEvent.change(screen.getByLabelText(/units/i), { target: { value: updatedUnits } });
    fireEvent.change(screen.getByLabelText(/goals \(json\)/i), { target: { value: JSON.stringify(updatedGoals) } });


    fireEvent.click(screen.getByRole('button', { name: /save changes/i }));

    await waitFor(() => {
      expect(userService.updateUserProfile).toHaveBeenCalledWith({
        ...mockUserProfile,
        name: updatedName,
        units: updatedUnits,
        goals: updatedGoals,
      });
      expect(screen.getByText(updatedName)).toBeInTheDocument();
      expect(screen.getByText(updatedUnits)).toBeInTheDocument();
      const updatedGoalsPreElement = screen.getByText('Goals:').nextElementSibling;
      expect(JSON.parse(updatedGoalsPreElement?.textContent || '{}')).toEqual(updatedGoals);
    });
  });

  test('cancels editing and reverts changes', async () => {
    (userService.getUserProfile as jest.Mock).mockResolvedValueOnce(mockUserProfile);
    render(<ProfilePage />);
    await waitFor(() => fireEvent.click(screen.getByRole('button', { name: /edit profile/i })));

    fireEvent.change(screen.getByLabelText(/name/i), { target: { value: 'Temporary Name' } });
    fireEvent.click(screen.getByRole('button', { name: /cancel/i }));

    await waitFor(() => {
      expect(screen.getByText(mockUserProfile.name)).toBeInTheDocument();
      expect(screen.queryByDisplayValue('Temporary Name')).not.toBeInTheDocument();
    });
  });
});

'use client';

import React, { useEffect, useState } from 'react';
import { getUserProfile, updateUserProfile, UserProfile, UserProfileUpdate } from '@/app/lib/api/user';
import { supabase } from '@/lib/supabaseClient';

export default function ProfilePage() {
  const [user, setUser] = useState<UserProfile | null>(null);
  const [formData, setFormData] = useState<UserProfileUpdate>({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    async function fetchProfile() {
      const { data: { session } } = await supabase.auth.getSession();

      if (session) {
        try {
          // Pass the access token to the API function
          const profile = await getUserProfile(session.access_token);
          setUser(profile);
          setFormData(profile);
        } catch (err: any) {
          setError(err.message);
        } finally {
          setLoading(false);
        }
      } else {
        setError("You are not authenticated.");
        setLoading(false);
      }
    }
    fetchProfile();
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleGoalsChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    try {
      const goals = JSON.parse(e.target.value);
      setFormData(prev => ({ ...prev, goals }));
    } catch (err) {
      // Handle invalid JSON input
      console.error("Invalid JSON for goals:", err);
      // Optionally set an error state for the goals field
    }
  };

  const handleEquipmentChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const equipmentArray = e.target.value.split(',').map(item => item.trim());
    setFormData(prev => ({ ...prev, equipment: equipmentArray }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const { data: { session } } = await supabase.auth.getSession();

    if (session) {
      try {
        const updatedProfile = await updateUserProfile(session.access_token, formData);
        setUser(updatedProfile);
        setIsEditing(false); // Exit editing mode on successful save
      } catch (err: any) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    } else {
      setError("You are not authenticated.");
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto p-4">
        <h1 className="text-3xl font-bold mb-6">User Profile</h1>
        <p>Loading profile...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto p-4">
        <h1 className="text-3xl font-bold mb-6">User Profile</h1>
        <p className="text-red-500">Error: {error}</p>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="container mx-auto p-4">
        <h1 className="text-3xl font-bold mb-6">User Profile</h1>
        <p>No user profile found.</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">User Profile</h1>

      {isEditing ? (
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-700">Name</label>
            <input
              type="text"
              name="name"
              id="name"
              value={formData.name || ''}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            />
          </div>
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email</label>
            <input
              type="email"
              name="email"
              id="email"
              value={formData.email || ''}
              readOnly // Email typically not editable
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm bg-gray-100 sm:text-sm"
            />
          </div>
          <div>
            <label htmlFor="goals" className="block text-sm font-medium text-gray-700">Goals (JSON)</label>
            <textarea
              name="goals"
              id="goals"
              value={JSON.stringify(formData.goals, null, 2)}
              onChange={handleGoalsChange}
              rows={5}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            ></textarea>
          </div>
          <div>
            <label htmlFor="preferences" className="block text-sm font-medium text-gray-700">Preferences (JSON)</label>
            <textarea
              name="preferences"
              id="preferences"
              value={JSON.stringify(formData.preferences, null, 2)}
              onChange={handleGoalsChange} // Re-using handleGoalsChange for preferences (assuming similar JSON structure)
              rows={5}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            ></textarea>
          </div>
          <div>
            <label htmlFor="equipment" className="block text-sm font-medium text-gray-700">Equipment (comma-separated)</label>
            <input
              type="text"
              name="equipment"
              id="equipment"
              value={Array.isArray(formData.equipment) ? formData.equipment.join(', ') : ''}
              onChange={handleEquipmentChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            />
          </div>
          <div>
            <label htmlFor="injuries" className="block text-sm font-medium text-gray-700">Injuries</label>
            <textarea
              name="injuries"
              id="injuries"
              value={formData.injuries || ''}
              onChange={handleChange}
              rows={3}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            ></textarea>
          </div>
          <div>
            <label htmlFor="units" className="block text-sm font-medium text-gray-700">Units</label>
            <input
              type="text"
              name="units"
              id="units"
              value={formData.units || ''}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            />
          </div>
          <div className="flex space-x-4">
            <button
              type="submit"
              className="inline-flex justify-center rounded-md border border-transparent bg-indigo-600 py-2 px-4 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
            >
              Save Changes
            </button>
            <button
              type="button"
              onClick={() => {
                setIsEditing(false);
                setFormData(user); // Reset form data if cancelling edit
              }}
              className="inline-flex justify-center rounded-md border border-gray-300 bg-white py-2 px-4 text-sm font-medium text-gray-700 shadow-sm hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
            >
              Cancel
            </button>
          </div>
        </form>
      ) : (
        <div className="space-y-4">
          <div>
            <p className="text-sm font-medium text-gray-700">Name:</p>
            <p className="mt-1 text-lg font-semibold">{user.name}</p>
          </div>
          <div>
            <p className="text-sm font-medium text-gray-700">Email:</p>
            <p className="mt-1 text-lg font-semibold">{user.email}</p>
          </div>
          <div>
            <p className="text-sm font-medium text-gray-700">Goals:</p>
            <pre className="mt-1 p-2 bg-gray-50 rounded-md text-sm">{JSON.stringify(user.goals, null, 2)}</pre>
          </div>
          <div>
            <p className="text-sm font-medium text-gray-700">Preferences:</p>
            <pre className="mt-1 p-2 bg-gray-50 rounded-md text-sm">{JSON.stringify(user.preferences, null, 2)}</pre>
          </div>
          <div>
            <p className="text-sm font-medium text-gray-700">Equipment:</p>
            <p className="mt-1 text-lg font-semibold">{Array.isArray(user.equipment) ? user.equipment.join(', ') : ''}</p>
          </div>
          <div>
            <p className="text-sm font-medium text-gray-700">Injuries:</p>
            <p className="mt-1 text-lg font-semibold">{user.injuries || 'N/A'}</p>
          </div>
          <div>
            <p className="text-sm font-medium text-gray-700">Units:</p>
            <p className="mt-1 text-lg font-semibold">{user.units || 'N/A'}</p>
          </div>
          <button
            onClick={() => setIsEditing(true)}
            className="inline-flex justify-center rounded-md border border-transparent bg-indigo-600 py-2 px-4 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
          >
            Edit Profile
          </button>
        </div>
      )}
    </div>
  );
}

